text:
/* 代码块样式 */
pre {
  margin-top: -0em;
  margin-bottom: -0em;
  margin-left: 0em;
  margin-right: 0em;
  padding: 2px 2px 2px 2px; /* 内边距上右下左 */
  border-radius: 3px;
}

/* 代码样式 */
code {
  margin-left: 0.2em;
  margin-right: 0.2em;
  padding: 2px 2px 1px 2px; /* 内边距上右下左 */
  border-radius: 3px;
  font-size: 0.9em;
}

/* 标题栏大小*/
.tc-titlebar {
	font-size:160%;
}

/* 标题大小 */
h2.tc-title {
	font-size:85%;
}

/* 页面标题和副标题大小 */
h1.tc-site-title, .jd-sitetitle {
	font-size: 100%;
}

.tc-site-subtitle, .jd-sitesubtitle {
	font-size: 90%;
}

/* 标题一标题二字体大小*/
h1 {
	font-size: 1.2em;
}

h2 {
	font-size: 1.1em;
}

/* 隐藏details summary的小箭头 */
details summary {
  display: block; 
  cursor: pointer; 
  background: rgba(0, 0, 0, 0); 
  border: none; 
  -webkit-tap-highlight-color: transparent;
  box-shadow: none;
}

summary {
  display: block; 
  cursor: pointer; 
  background: rgba(0, 0, 0, 0); 
  border: none; 
  -webkit-tap-highlight-color: transparent;
  box-shadow: none;
}

/*  修改缩进块格式  */
blockquote{
  border: none;
  margin-left: 0;
  padding: 0;
}

/* 清除移动端半透明阴影边框 */
.tc-table-of-contents, .tc-tag-label, button{
  -webkit-tap-highlight-color: transparent;
  box-shadow: none;
}

a {
  -webkit-tap-highlight-color: transparent; /* 去除移动端点击高亮 */
  box-shadow: none; /* 去除盒阴影 */
  text-underline-offset: 0.2em; /* 设置下划线与文字间距 */
  color: initial; /* 默认文字颜色 */
  transition: color 0.3s; /* 平滑过渡效果 */
}

a:hover {
  color: #3C82C3;
}
